import React, { useState } from 'react';
import './App.css';

function App() {
    const [income, setIncome] = useState('');
    const [writeOffs, setWriteOffs] = useState('');
    const [superFunds, setSuperFunds] = useState('');
    const [taxLiabilityOld, setTaxLiabilityOld] = useState(null);
    const [taxLiabilityNew, setTaxLiabilityNew] = useState(null);

    const calculateTax = (income, writeOffs, superFunds, rates) => {
        const taxableIncome = income - writeOffs - superFunds;
        let tax = 0;

        if (taxableIncome <= 18200) {
            tax = 0;
        } else if (taxableIncome <= 45000) {
            tax = (taxableIncome - 18200) * rates[0];
        } else if (taxableIncome <= 120000) {
            tax = (45000 - 18200) * rates[0] + (taxableIncome - 45000) * rates[1];
        } else if (taxableIncome <= 135000) {
            tax = (45000 - 18200) * rates[0] + (120000 - 45000) * rates[1] + (taxableIncome - 120000) * rates[2];
        } else if (taxableIncome <= 190000) {
            tax = (45000 - 18200) * rates[0] + (120000 - 45000) * rates[1] + (135000 - 120000) * rates[2] + (taxableIncome - 135000) * rates[3];
        } else {
            tax = (45000 - 18200) * rates[0] + (120000 - 45000) * rates[1] + (135000 - 120000) * rates[2] + (190000 - 135000) * rates[3] + (taxableIncome - 190000) * rates[4];
        }
        return tax;
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        const oldRates = [0.19, 0.325, 0.37, 0.45, 0.47];
        const newRates = [0.16, 0.30, 0.37, 0.45, 0.47];

        const oldTax = calculateTax(income, writeOffs, superFunds, oldRates);
        const newTax = calculateTax(income, writeOffs, superFunds, newRates);

        setTaxLiabilityOld(oldTax);
        setTaxLiabilityNew(newTax);
    };

    return (
        <div className="container">
            <h1>Australian Tax Calculator</h1>
            <form onSubmit={handleSubmit}>
                <label htmlFor="income">Total Income:</label>
                <input
                    type="number"
                    id="income"
                    value={income}
                    onChange={(e) => setIncome(e.target.value)}
                    required
                />

                <label htmlFor="writeOffs">Write-offs:</label>
                <input
                    type="number"
                    id="writeOffs"
                    value={writeOffs}
                    onChange={(e) => setWriteOffs(e.target.value)}
                    required
                />

                <label htmlFor="superFunds">Super Funds:</label>
                <input
                    type="number"
                    id="superFunds"
                    value={superFunds}
                    onChange={(e) => setSuperFunds(e.target.value)}
                    required
                />

                <button type="submit">Calculate Tax</button>
            </form>
            {taxLiabilityOld !== null && taxLiabilityNew !== null && (
                <div className="results">
                    <h2>Comparison</h2>
                    <div className="result">
                        <div className="oldTax">
                            <h3>2023â€“24 settings</h3>
                            <p>Estimated annual tax liability: ${taxLiabilityOld.toFixed(2)}</p>
                        </div>
                        <div className="newTax">
                            <h3>Under new tax cuts</h3>
                            <p>Estimated annual tax liability: ${taxLiabilityNew.toFixed(2)}</p>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
}

export default App;
